import UIKit

class ViewController: UIViewController {
    
    let colors: [UIColor] = [.systemRed, .systemBlue, .systemCyan, .systemGray, .systemPink, .systemTeal]
    
    let pictures: [String] = [ "data:image/jpeg;base64,",
        
        "data:image/jpeg;base64,",
                                
        "data:image/jpeg;base64,",
                                
        "data:image/jpeg;base64,",
                                
        "data:image/jpeg;base64,",
                                
        "data:image/jpeg;base64,"]

    private let imageView: UIImageView = {
        let imageView = UIImageView()
        imageView.contentMode = .scaleToFill
        imageView.backgroundColor = .white
        return imageView
    }()
    
    private let button: UIButton = {
        let button = UIButton()
        button.backgroundColor = .white
        button.setTitleColor(.black, for: .normal)
        button.setTitle("Generate Meme", for: .normal)
        return button
    }()
    
    override func viewDidLoad() {//kevintest
        super.viewDidLoad()
        
        view.backgroundColor = .systemPink
        view.addSubview(imageView)
        imageView.frame = CGRect(x: 0,
                                 y: 0,
                                 width: 300,
                                 height: 300)
        
        imageView.center = view.center
        
        view.addSubview(button)
        getRandomPhoto()
        button.addTarget(self, action: #selector(didTapButton), for: .touchUpInside)
    }
    
    @objc func didTapButton() {
        getRandomPhoto()
        
        view.backgroundColor = colors.randomElement()
    }
    
    override func viewDidLayoutSubviews() {
        super.viewDidLayoutSubviews()
        
        button.frame = CGRect(x: 30,
                              y: view.frame.size.height - 150 - view.safeAreaInsets.bottom,
                              width: view.frame.size.width - 60,
                              height: 55)
    }
    
    private func getRandomPhoto() {
        
        let urlString = pictures.randomElement()
        
        let url = URL(string: urlString!)!
        guard let data = try? Data(contentsOf: url) else {
            return
        }
        
        imageView.image = UIImage(data: data)
        
    }

}

